class Log extends BaseLog
{
   /*
  * Atualizar Log anterior
  */
  public function atualizar()
  {
    $this->setDataHoraSaida(date('Y-m-d H:i:s'));
    $this->save();
  }


  /*
   * Busca nome e sobrenome baseado do usuÃ¡rio 
   */
   public function buscaNomeUsuario($user)
   {
	 
      $q = Doctrine_Query::create()
         ->select('first_name, last_name')
         ->from('sfGuardUser')
		 ->where('username = ?', $user);
	 
		 return $q->fetchOne();
   }
}
