    */
   public function addServicosQuery(Doctrine_Query $q = null, $id)
   {
     if (is_null($q))
     {
      $q = Doctrine_Query::create()
         ->from('Servico_item j');
     }

     $alias = $q->getRootAlias();
     $q->andWhere($alias.'.orcamento_id = ?', $id);

      return $q;
   }

