        return $this->addItemServicoQuery($q)->execute();
    } 

   /*
    * Buscar item servico
    */
    protected function addItemServicoQuery (Doctrine_Query $q = null){

      if (is_null($q)) {
        $q = Doctrine_Query::create()
          ->from('Servico_item j');
      }

      $alias = $q->getRootAlias();

      return $q;
    }

